import React, { useState, useEffect } from 'react';
import { getAllProducts, updateProduct } from '../../services/Product';

const ProductModify = () => {
    const [products, setProducts] = useState([]);
    const [loading, setLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState('');
    const [hideList, setHideList] = useState(false);
    const [selectedProduct, setSelectedProduct] = useState(null);
    const [modifiedProduct, setModifiedProduct] = useState({productName: '', price: '', image: ''});

    useEffect(() => {
        getProducts();

    }, []);

    const getProducts = async () => {
        try {
            const productsData = await getAllProducts();
            setProducts(productsData);
        } catch (error) {
            setErrorMessage(error.message);
        } finally {
            setLoading(false);
        }
    };

    const handleModify = (product) => {
        setSelectedProduct(product);
        setHideList(true);
        setModifiedProduct({
            productName: product.productName,
            price: product.price,
            image: product.image
        });
    };

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setModifiedProduct(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            await updateProduct(selectedProduct.productName, modifiedProduct);
            await getProducts();
            setSelectedProduct(null);
            setModifiedProduct({ productName: '', price: '', image: '' });
        } catch (error) {
            setErrorMessage('Error al modificar el producto');
        }
    };

    if (loading) return <p>Cargando productos...</p>;
    if (errorMessage) return <p className="error-message">{errorMessage}</p>;

    return (
        <div className="modify-product-container">
            <h2>Modificar Productos</h2>
            <ul className="products-list">
                {products.map((product) => (
                    <li key={product.productName} className="product-item">
                        <span>{product.productName} - {product.price.toFixed(2)} UPCoin</span>
                        <button onClick={() => handleModify(product)}>Modificar</button>
                    </li>
                ))}
            </ul>

            {selectedProduct && (
                <form onSubmit={handleSubmit} className="modify-form">
                    <h3>Modificar {selectedProduct.productName}</h3>
                    <div>
                        <label htmlFor="productName">Nombre del Producto:</label>
                        <input type="text" id="productName" value={modifiedProduct.productName} onChange={handleInputChange}/>
                    </div>
                    <div>
                        <label htmlFor="price">Precio:</label>
                        <input type="number" id="price" value={modifiedProduct.price} onChange={handleInputChange}/>
                    </div>
                    <div>
                        <label htmlFor="image">URL de la Imagen:</label>
                        <input type="text" id="image" value={modifiedProduct.image}onChange={handleInputChange}/>
                    </div>
                    <button type="submit">Guardar Cambios</button>
                    <button type="button" onClick={() => setSelectedProduct(null)}>Cancelar</button>
                </form>
            )}
        </div>
    );
};

export default ProductModify;
