import axios from 'axios';

const API_URL = '???????????????????';

export const getAllProducts = async () => {
    try {
        /*const response = await axios.get(`${API_URL}/product`);
        return response.data;*/

        const products = [
            { productName: "Producto 1", price: 10.00 },
            { productName: "Producto 2", price: 15.50 },
            { productName: "Producto 3", price: 20.99 },
            { productName: "Producto 4", price: 5.00 }
        ];
        return products;

    } catch (error) {
        console.error("Error al obtener los productos: ", error);
        throw new Error ('No se han podido obtener los productos');
    }
};

export const addProduct = async (product) => {
    try {
        const response = await axios.post(`${API_URL}/product`, product);
        return response.data;
    } catch (error) {
        console.error("Error al a単adir el producto", error);
        throw new Error('No se ha podido a単adir el producto');
    }
};

export const deleteProduct = async (productName) => {
    try {
        await axios.delete(`${API_URL}/product/${productName}`);
    } catch (error) {
        console.error("Error al eliminar el producto", error);
        throw new Error('No se ha podido eliminar el producto');
    }
};
