import React, { useState } from 'react';
import ProductItem from './ProductItem';
import BlockchainPayment from '../Payments/BlockchainPayment';

const ProductCarrito = ({ itemsCarrito, onRemoveFromCarrito}) => {
    const [isPaymentVisible, setIsPaymentVisible] = useState(false);

    if (itemsCarrito.length === 0) {
        return <p>El carrito esta vacio</p>;
    }

    const totalAmount = itemsCarrito.reduce((total, product) => total + product.price, 0).toFixed(2);

    const handlePayment = () => {
        setIsPaymentVisible(true);
    };

    return(
        <div className='products-carrito'>
            <h2>Tu carrito</h2>
            <div className='items-carrito'>
                {itemsCarrito.map((product) => (
                   <div key={product.productName} className='item'>
                        <ProductItem product={product} />
                        <button onClick={() => onRemoveFromCarrito(product.productName)}>Eliminar</button>
                   </div> 
                ))}
            </div>
            <h3>Importe total: ${totalAmount}</h3>
            <button onClick={handlePayment}>Pagar</button>
            {isPaymentVisible && (<BlockchainPayment totalAmount={totalAmount} onClose={() => setIsPaymentVisible(false)} /> )}
        </div>
    );
};

export default ProductCarrito;
