import React, {useState} from 'react';
import ProductList from '../components/Products/ProductList';
import ProductsCarrito from '../components/Products/ProductCarrito';

const ShopPage = () => {
    const [itemsCarrito, setItemsCarrito] = useState([]);
    const [show, setShow] = useState(true);


    const handleShow = () => {
        setShow(show);
        setShow(!show);
    }


    const handleAddToCarrito = (productName) => {
        const productToAdd = itemsCarrito.find(product => product.productName === productName);

        if (productToAdd) {
            setItemsCarrito([...itemsCarrito, productToAdd]);
        }
    };

    const handleRemoveFromCarrito = (productName) => {
        setItemsCarrito(itemsCarrito.filter(product => product.productName !== productName));
    };

    return(
        <div>
            <button onClick={handleShow}>{show ? 'Ver Carrito' : 'Ver Productos'}</button>

            {show && (
            <ProductList onAddToCarrito={handleAddToCarrito} />
            )}
            {!show && (
            <ProductsCarrito itemsCarrito={itemsCarrito} onRemoveFromCarrito={handleRemoveFromCarrito} />
            )}
        </div>
    );
};

export default ShopPage;
