import React, { useState } from 'react';

const ProductItem = ({product, onAddToCarrito, showAddButton}) => {
    const {productName, price, quantity, image} = product;
    const [amount, setAmount] = useState(quantity ? quantity : 1);
    const [totalPrice, setTotalPrice] = useState('');

    const increment = () => {
        setAmount(amount + 1);
    }

    const decrement = () => {
        if (amount > 1) setAmount(amount - 1);
    }

    const computePrice = () => {
        setTotalPrice(parseFloat({price})*{amount});
    }

    return(
        <div className='product-item'>
            <img src={image} alt='' />
            <h3>{productName}</h3>
            <p>Precio: {totalPrice}</p>{/*Multiplicar precio*/}
            {!showAddButton && (<p>Cantidad: {amount}</p>)}
            {showAddButton && (
                <div>
                    <div>
                        <button onClick={decrement} disabled={amount === 1}>-</button>
                        <span>{amount}</span>
                        <button onClick={() => {increment(); computePrice();}}>+</button>
                    </div>
                    <button onClick={() => onAddToCarrito (product, amount)}>AÃ±adir al Carrito</button>
                </div>
                )}

        </div>


    );
};

export default ProductItem;
